mapper.select:
select * from ( select i.fid,i.fname,count(w.fid) total from tb_insframework i inner join tb_welding_machine w on w.finsframework_id=i.fid where i.ftype=23
and i.fparent=#{parent}
group by i.fid UNION select i.fid,i.fname,0 total from tb_insframework i where i.ftype=23
and i.fparent=#{parent}
and i.fid not in (select i.fid from tb_insframework i inner join tb_welding_machine w on w.finsframework_id=i.fid where i.ftype=23
and i.fparent=#{parent}
group by i.fid ) )temp order by fid

select count(*) from(
select fmachine_id from tb_work where 1=1

	and fitemid= #{itemid}


	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

UNION 
select fmachine_id from tb_standby where 1=1

	and fitemid= #{itemid}


	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

UNION
select fmachine_id from tb_alarm where 1=1

	and fitemid= #{itemid}


	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

)temp

select count(DISTINCT(fmachine_id)) machinenum
from tb_work where 1=1

	and fitemid= #{itemid}


	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}

select count(fjunction_id) junctionnum from(
select fjunction_id from tb_work where 1=1

	and fitemid= #{itemid}


	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

union
select fjunction_id from tb_standby where 1=1

	and fitemid= #{itemid}


	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

)temp

select sum(num) from(
select sum(fworktime) num from tb_work where 1=1

	and fitemid= #{itemid}


	
		and fjunction_id = #{dto.junctionno}
	
	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

UNION ALL
select sum(fstandbytime) num from tb_standby where 1=1

	and fitemid= #{itemid}


	
		and fjunction_id = #{dto.junctionno}
	
	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

UNION ALL
select sum(falarmtime) num from tb_alarm where 1=1

	and fitemid= #{itemid}


	
		and fjunction_id = #{dto.junctionno}
	
	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

)temp

select sum(fstandbytime) worktime from tb_standby where 1=1 

	and fitemid= #{itemid}


	
		and fjunction_id = #{dto.junctionno}
	
	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}

SELECT * from (
select avg(felectricity) electricity,avg(fvoltage) voltage,sum(fworktime) worktime from tb_work where 1=1 

	and fitemid= #{itemid}


	
		and fjunction_id = #{dto.junctionno}
	
	
		and fwelder_id = #{dto.welderno}
	
	
		and fmachine_id = #{dto.machineid}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

)temp

select * from ( select i.fid,i.fname,SUM(w.fworktime) insid from tb_insframework i inner join tb_work w on w.fitemid=i.fid where i.ftype=23
and w.fstarttime >= #{dto.dtoTime1}
and w.fendtime <= #{dto.dtoTime2}
group by i.fid UNION select i.fid,i.fname,0 id from tb_insframework i where i.ftype=23 and i.fid not in (select i.fid from tb_insframework i inner join tb_work w on w.fitemid=i.fid where i.ftype=23
and w.fstarttime >= #{dto.dtoTime1}
and w.fendtime <= #{dto.dtoTime2}
group by i.fid ) )temp

select * from ( select i.fid,i.fname,SUM(w.falarmtime) insid from tb_insframework i inner join tb_alarm w on w.fitemid=i.fid where i.ftype=23
and w.fstarttime >= #{dto.dtoTime1}
and w.fendtime <= #{dto.dtoTime2}
group by i.fid UNION select i.fid,i.fname,0 insid from tb_insframework i where i.ftype=23 and i.fid not in (select i.fid from tb_insframework i inner join tb_alarm w on w.fitemid=i.fid where i.ftype=23
and w.fstarttime >= #{dto.dtoTime1}
and w.fendtime <= #{dto.dtoTime2}
group by i.fid ) )temp

select * from (
SELECT m.fequipment_no fname,fname insname,SUM(w.fworktime) insid FROM tb_welding_machine m 
inner join tb_insframework i on i.fid = m.finsframework_id 
INNER JOIN tb_work w ON w.fmachine_id=m.fid 
where 1=1

	
		and w.fstarttime >= #{dto.dtoTime1}
	
	
		and w.fendtime <= #{dto.dtoTime2}
	


	and m.finsframework_id= #{itemid}

GROUP BY m.fid
UNION
SELECT m.fequipment_no fname,i.fname insname,0 insid FROM tb_welding_machine m 
inner join tb_insframework i on i.fid = m.finsframework_id where
    m.fid NOT IN (SELECT m.fid FROM tb_welding_machine m 
    inner join tb_insframework i on i.fid = m.finsframework_id 
    INNER JOIN tb_work w ON w.fmachine_id=m.fid where 1=1 
	
		
			and w.fstarttime >= #{dto.dtoTime1}
		
		
			and w.fendtime <= #{dto.dtoTime2}
		
	
	
		and m.finsframework_id= #{itemid}
	
    GROUP BY m.fid )
	
		and m.finsframework_id= #{itemid}
	
)temp

select * from ( SELECT m.fequipment_no fname,fname insname,SUM(w.falarmtime) insid FROM tb_welding_machine m inner join tb_insframework i on i.fid = m.finsframework_id INNER JOIN tb_alarm w ON w.fmachine_id=m.fid where 1=1
and w.fstarttime >= #{dto.dtoTime1}
and w.fendtime <= #{dto.dtoTime2}
GROUP BY m.fid UNION SELECT m.fequipment_no fname,i.fname insname,0 insid FROM tb_welding_machine m inner join tb_insframework i on i.fid = m.finsframework_id where m.fid NOT IN (SELECT m.fid FROM tb_welding_machine m inner join tb_insframework i on i.fid = m.finsframework_id INNER JOIN tb_alarm w ON w.fmachine_id=m.fid where 1=1
and w.fstarttime >= #{dto.dtoTime1}
and w.fendtime <= #{dto.dtoTime2}
GROUP BY m.fid ) )temp

select * from ( SELECT w.fwelder_no insname,w.fname,SUM(a.fworktime) insid FROM tb_welder w inner join tb_work a on a.fwelder_id=w.fwelder_no where 1=1
and a.fstarttime >= #{dto.dtoTime1}
and a.fendtime <= #{dto.dtoTime2}
GROUP BY w.fid UNION SELECT w.fwelder_no insname,w.fname,0 insid FROM tb_welder w where w.fid NOT IN ( SELECT w.fid FROM tb_welder w inner join tb_work a on a.fwelder_id=w.fwelder_no where 1=1
and a.fstarttime >= #{dto.dtoTime1}
and a.fendtime <= #{dto.dtoTime2}
GROUP BY w.fid ) )temp

select * from ( SELECT w.fwelder_no insname,w.fname,SUM(a.falarmtime) insid FROM tb_welder w inner join tb_alarm a on a.fwelder_id=w.fwelder_no where 1=1
and a.fstarttime >= #{dto.dtoTime1}
and a.fendtime <= #{dto.dtoTime2}
GROUP BY w.fid UNION SELECT w.fwelder_no insname,w.fname,0 insid FROM tb_welder w where w.fid NOT IN ( SELECT w.fid FROM tb_welder w inner join tb_alarm a on a.fwelder_id=w.fwelder_no where 1=1
and a.fstarttime >= #{dto.dtoTime1}
and a.fendtime <= #{dto.dtoTime2}
GROUP BY w.fid ) )temp

select * from (
SELECT j.fwelded_junction_no insname,SUM(a.fworktime) insid FROM tb_welded_junction j 
INNER JOIN tb_work a ON j.fwelded_junction_no=a.fjunction_id WHERE 1=1 

	
		and a.fstarttime >= #{dto.dtoTime1}
	
	
		and a.fendtime <= #{dto.dtoTime2}
	


	and j.fwelded_junction_no like #{junctionno}

    GROUP BY j.fid
UNION
SELECT j.fwelded_junction_no insname,0 insid FROM tb_welded_junction j where
    j.fid NOT IN (
    SELECT j.fid FROM tb_welded_junction j 
    INNER JOIN tb_work a ON j.fwelded_junction_no=a.fjunction_id WHERE 1=1 

	
		and a.fstarttime >= #{dto.dtoTime1}
	
	
		and a.fendtime <= #{dto.dtoTime2}
	


	and j.fwelded_junction_no like #{junctionno}

    GROUP BY j.fid)

	and j.fwelded_junction_no like #{junctionno}

)temp

select * from (
SELECT j.fwelded_junction_no insname,SUM(a.falarmtime) insid FROM tb_welded_junction j 
INNER JOIN tb_alarm a ON j.fwelded_junction_no=a.fjunction_id WHERE 1=1 

	
		and a.fstarttime >= #{dto.dtoTime1}
	
	
		and a.fendtime <= #{dto.dtoTime2}
	


	and j.fwelded_junction_no like #{junctionno}

    GROUP BY j.fid   
UNION
SELECT j.fwelded_junction_no insname,0 insid FROM tb_welded_junction j 
where j.fid NOT IN (
SELECT j.fid FROM tb_welded_junction j 
INNER JOIN tb_alarm a ON j.fwelded_junction_no=a.fjunction_id 
WHERE 1=1 

	
		and a.fstarttime >= #{dto.dtoTime1}
	
	
		and a.fendtime <= #{dto.dtoTime2}
	


	and j.fwelded_junction_no like #{junctionno}

    GROUP BY j.fid)

	and j.fwelded_junction_no like #{junctionno}

)temp

SELECT m.fid,m.fequipment_no fname,i.fname insname,d.fvaluename,sum(falarmtime) num FROM tb_warn l 
INNER JOIN tb_welding_machine m on m.fid = l.fmachine_id 
INNER JOIN tb_insframework i on i.fid = m.finsframework_id 
INNER JOIN tb_dictionary d on d.fvalue = l.fstatus
where d.ftypeid=15 

	and d.fvalue=#{value}


	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

group by m.fid

SELECT m.fequipment_no fname,i.fname insname,d.fvaluename,FWeldTime time FROM tb_live_data l 
INNER JOIN tb_welding_machine m ON m.fid = l.fmachine_id 
INNER JOIN tb_insframework i on i.fid = m.finsframework_id 
INNER join tb_dictionary d on d.fvalue=l.fstatus 
where d.ftypeid=15

	and fmachine_id=#{id}


	and d.fvalue=#{value}


	
		and FWeldTime >= #{dto.dtoTime1}
	
	
		and FWeldTime <= #{dto.dtoTime2}

select sum(fworktime) /60/60 hour,w.fname,w.fwelder_no welderno,i.fname insname FROM tb_work l left JOIN tb_welder w on w.fwelder_no = l.fwelder_id INNER join tb_insframework i on i.fid = l.fitemid INNER join tb_insframework ins on ins.fid = i.fparent where fstarttime >= #{time} and fendtime < date_sub(date_sub(#{time},interval -1 day),interval 0 minute)
AND ins.fid=#{parent}
group by w.fname ORDER by hour LIMIT 10

select count(DISTINCT(fmachine_id)) machinenum from tb_work where 1=1
and fitemid= #{itemid}
and fstarttime >= #{time} and fendtime < date_sub(date_sub(#{time},interval -1 day),interval 0 minute)

select * from (select i.fid,i.fname,SUM(fworktime)/60/60 hour,DATE_FORMAT( fstarttime,'%Y-%m-%d' ) time from tb_insframework i 
inner join tb_work w on w.fitemid=i.fid
where i.ftype=23

	
		and i.fparent >= #{dto.parent}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

group by i.fid,time
)temp

select * from (
select i.fid,i.fname,SUM(w.falarmtime)/60/60 hour,DATE_FORMAT( fstarttime,'%Y-%m-%d' ) time from tb_insframework i 
inner join tb_alarm w on w.fitemid=i.fid
where i.ftype=23

	
		and i.fparent >= #{dto.parent}
	
	
		and fstarttime >= #{dto.dtoTime1}
	
	
		and fendtime <= #{dto.dtoTime2}
	

group by i.fid,time
)temp
