mapper.select:
		select sum(time) from (
		select sum(fworktime) time from tb_work l 
		where fstarttime >= #{time} and fendtime <= date_sub(date_sub(#{time},interval -1 day),interval 0 minute)

			and fitemid = #{id}

     	UNION ALL
     	select sum(fstandbytime) time from tb_standby l 
		where fstarttime >= #{time} and fendtime <= date_sub(date_sub(#{time},interval -1 day),interval 0 minute)

			and fitemid = #{id}

		)temp
